int LoadDefaultResource()
{
  if(default_resource)
      return 1;
  else
      default_resource = (DEFAULT_RESOURCE *)malloc(sizeof(DEFAULT_RESOURCE));
  if(!default_resource)
      return 0 ;
  memcpy(default_resource,&default_data,sizeof(DEFAULT_RESOURCE));
  default_resource->syllable_map_items =  sizeof(default_resource->syllable_map) /sizeof(default_resource->syllable_map[0]);
  return 1;
}

int FreeDefaultResource()
{
    if (default_resource)
    {
        free(default_resource);
        default_resource = 0;
    }

    return 1;
}
